import path from "path"; // to use it make modification in the package.json

//BASENAME
console.log(path.basename("c:\\path\\directory\\basename"))// in this the path.basename provides the last name of the path here basename is the last name so it provides that 

//DIRNAME
console.log(path.dirname("c:\\path\\directory\\basename"))
// Gives the directory name as the output
console.log(path.dirname("c:directory\\basename"))// the folder name that is inside the c drive and directory it is there 

//EXTNAME
console.log(path.extname("c:\\path\\directory\\userInfo.js"))

//JOIN
console.log(path.join("c:","sutapa","courses","lowest"))//joins the paths 
//FOR GOING UP a level use(..) for how many levels you want to go up
console.log(path.join("c:","sutapa","courses","lowest",".."))

//NORMALISE
console.log(path.normalize("c:\\\\nodejs\\\\courses\\app.js"))// used to remove .. and . also if multiple slashes are founf they remove it and make it single slashes trailing slashes are preserved.

//PATH.PARSE
console.log(path.parse("c:\\nodejs\\courses\\app.js").base)
console.log(path.parse("c:\\nodejs\\courses\\app.js").name)
console.log(path.parse("c:\\nodejs\\courses\\app.js").ext)

